import React, { useState } from 'react'
import axios from 'axios';
import NavigationBar from './NavigationBar';

function Registration() {

    const [ename, setEmpName] = useState("");
    const [eemail, setEmpEmail] = useState("");;
    const [emobile, setEmpmobile] = useState("");
    const [edob, setEmpDOB] = useState("");;
    const [epass, setEmpPass] = useState("");
    const [egender, setEmpGender] = useState("");;
    const [ecountry, setEmpCountry] = useState("");
    const [eaddress, setEmpAddress] = useState("");;
    const [msg, setMessage] = useState("");;

    const onChangeEmpName = (e) => setEmpName(e.target.value);
    const onChangeEmpRoll= (e) => setEmpEmail(e.target.value);
    const onChangeSub1 = (e) => setEmpmobile(e.target.value);
    const onChangeSub2 = (e) => setEmpDOB(e.target.value);
    const onChangesub3 = (e) => setEmpPass(e.target.value);
   

    const handleSubmit = (evt) => {
        evt.preventDefault();
        console.log(`Form submitted:`);
        console.log(`NAME: ${ename}`);
        console.log(`EMAIL: ${eroll}`);

        const empinfo = {
            empname: ename,
            empemail: eroll,
            empmobile: esub1,
            empdob: esub2,
            emppass: esub3,
            
        }

        axios.post('http://localhost:4500/emp/register', empinfo)
            .then(res => {
                console.log(res.data)
                setMessage('REGISTRATION SUCCESSFUL')
            });

        setEmpName('')
        setEmpEmail('')
        setEmpmobile('')
        setEmpDOB('')
        setEmpPass('')
        setEmpGender('')
        setEmpCountry('')
        setEmpAddress('')

    }

    return (
        <div>
            <NavigationBar />
            <br />
            <h3>REGISTRATION FORM</h3>
            <h4 style={{ color: "brown" }}> {msg}</h4>
            <form onSubmit={handleSubmit}>
                <input type="text" value={ename}
                    onChange={onChangeEmpName} placeholder="Enter Name"
                    required />
                <br /><br />

                <input type="number" value={eroll}
                    onChange={onChangeEmpRoll} placeholder="Enter roll"
                    required />
                <br /><br />

                <input type="number" value={esub1}
                    onChange={onChangeSub1} placeholder="Enter sub1 attendance"
                    required />
                <br /><br />

                <input type="number" value={esub2}
                    onChange={onChangeSub2} placeholder="Enter sub2 attendance"/>
                <br /><br />

                <input type="number" value={esub3}
                    onChange={onChangeSub3} placeholder="Enter sub3 attendance"
                    required /> 
                <br /><br />


                <input type="submit" value="REGISTER" />

            </form>
        </div>
    )
}


export default Registration
